function getColor(d) {
	return d > 30  ? '#B20000' :
		   d > 25  ? '#F20000' :
		   d > 20  ? '#FF5500' :
		   d > 15  ? '#FD8700' :
		   d > 10  ? '#FDBC30' :
		   d > 5   ? '#A5C8FC' :
		   d > 1   ? '#4ABAFB' :
		   d < (-15) ? '#00014B' :
		   d < (-10) ? '#000985' :
		   d < (-5)  ? '#2036DA' :
		   d < 1  ? '#0676FF' :
					 '#FFFFFF' ;
}


// Légende des couleurs en fonction des températures

var LegendTemperature = L.control({position: 'bottomright'});
LegendTemperature.onAdd = function (mapPays) {
    var div = L.DomUtil.create('div', 'info legend'),
        grades = [(-15), (-10), (-5), 5, 10, 15, 20, 25, 30],
        labels = [];

    // loop through our density intervals and generate a label with a colored square for each interval
    for (var i = 0; i < grades.length; i++) {
        div.innerHTML +=
            '<i style="background:' + getColor(grades[i]+1) + '"></i> ' +
            grades[i] + (grades[i + 1] ? '&ndash;' + grades[i + 1] + '</br></br>' : '+');
    }

    return div;
};

LegendTemperature.addTo(mapPays);

// Set du mouseover et out
function onEachFeaturePays(feature, layer) {
	layer.on({
		mouseover: highlightFeaturePays,
		mouseout: resetHighlightPays,
	});
}

// Affiche les informations du pays
function highlightFeaturePays(e) {
	var layer = e.target;
    layer.setStyle({
        weight: 1,
        color: layer.feature.properties.Color,
        dashArray: '',
        fillOpacity: 1
    });
	infoPays.update(e.target.feature.properties.Text, layer.feature.properties.Color);
}

// Efface les informations du pays
function resetHighlightPays(e) {
	info.update("");
	var layer = e.target;
    layer.setStyle({
        opacity: 1,
        weight: 0.2,
		color: layer.feature.properties.Color,
		fillOpacity: 0
    });
}

// Efface les bordures bleu par défaut
function stylePays(feature) {
	return {
		opacity: 1,
        weight: 0.2,
		color: "black",
		fillOpacity: 0
	};
	this.infoBulle.style.color = "#777";
}

//Supprime un élement du DOM
function remove(id) {
    return (elem=document.getElementById(id)).parentNode.removeChild(elem);
}

function correspondAPays(id){
	for(var i=0; i<correspondance.length; i++){
		var c = correspondance[i];
		if(c[1] === id)
			return c[0];
	}
}
// Évennement mouvement souri
document.onmousemove = getMouseXY;

// Applique à la tooltip la position de la souri
//(dans le cas ou la tooltip est affichée)
function getMouseXY(e) {
  tempX = e.pageX;
  tempY = e.pageY;

  // catch possible negative values in NS4
  if (tempX < 0){tempX = 0}
  if (tempY < 0){tempY = 0}  
  
  if(document.getElementById("tooltip") !== null){
    document.getElementById("tooltip").style.top = tempY + 10 +"px";
    document.getElementById("tooltip").style.left = tempX + 10 + "px";
  }
  
  return true;
}

//Affiche la tooltip
function displayTooltip(text){
  var body = document.getElementsByTagName("body")[0];
  
  var tooltip = document.createElement("div");
  tooltip.className = "tooltip";
  tooltip.id = "tooltip";
  tooltip.innerHTML = text;
  body.appendChild(tooltip);
}

//Cache la tooltip
function hideTooltip(){
  remove("tooltip");
}

function convertDate(inputFormat) {
  function pad(s) { return (s < 10) ? '0' + s : s; }
  var d = new Date(inputFormat);
  return [pad(d.getDate()), pad(d.getMonth()+1), d.getFullYear()].join('/');
}